package Ibne_7;

//Using lowerCase/upperCase

public class Shafin_15 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String x="PLEASE PRAY 5 TIMES A DAY";
		System.out.println("String x lower case is :"+ x.toLowerCase());
		
		String y="please pray 5 times a day";
		System.out.println("String y upper case is :"+ y.toUpperCase());

	}

}
