package Ibne_7;

public class Shafin_5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int S = 55;
		System.out.println("In String  System N : " + Integer.toString(S));
		System.out.println("In Decimal System N : " + S);
		System.out.println("In Binary  System N : " +   Integer.toBinaryString(S));
		System.out.println("In Octal   System N : " + Integer.toOctalString(S));
		System.out.println("In Hex.    System N : " + Integer.toHexString(S));

	}

}
