package WP_7;

// using setCharAT

public class Web_13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		StringBuilder s=new StringBuilder("Adira");
		System.out.println("String s is:"+" "+s);
		System.out.println("Character at index 3 is:"+" "+s.charAt(3));
		s.setCharAt(3, 'b');
		System.out.println("After add b");
		
		
		System.out.println("Character at index 3 is:"+" "+s.charAt(3));
		
		System.out.println("String s is:"+" "+s);
		
		

	}

}
